 	set LEAVE_REASON=#{leaveReason}, status='N'
WHERE USER_NO=#{userNo}


UPDATE MEMBER 
	set USER_PWD=#{userPwd}
WHERE  USER_PWD=#{userPwd}
AND USER_ID=#{userId}

